#include <stdio.h>

int correctA(char n[], char guess[]) //判断位置正确的个数 
{
	int count = 0, i;
	for (i = 0; i < 4; i++)
	{
		if (n[i] == guess[i])
		{
			count++;
		}
	}
	return count;
}

int correctB(char n[], char guess[]) // 判断数字正确的个数 
{
	int count = 0, i, j;
	for (i = 0; i < 4; i++)
	{
		for (j = 0; j < 4; j ++)
		{
			if (guess[j] == n[i])
			{
				count++;
			}
		}
	}
	return count - correctA(n, guess);
}
